// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, onSnapshot } from "firebase/firestore";

function AdminPage() {
  const [mealCounts, setMealCounts] = useState({
    breakfast: 0,
    lunch: 0,
    supper: 0,
  });

  useEffect(() => {
    // listen to changes in breakfast
    const unsubBreakfast = onSnapshot(doc(db, "meals", "breakfast"), (docSnap) => {
      setMealCounts((prev) => ({
        ...prev,
        breakfast: docSnap.exists() ? docSnap.data().count || 0 : 0,
      }));
    });

    // listen to changes in lunch
    const unsubLunch = onSnapshot(doc(db, "meals", "lunch"), (docSnap) => {
      setMealCounts((prev) => ({
        ...prev,
        lunch: docSnap.exists() ? docSnap.data().count || 0 : 0,
      }));
    });

    // listen to changes in supper
    const unsubSupper = onSnapshot(doc(db, "meals", "supper"), (docSnap) => {
      setMealCounts((prev) => ({
        ...prev,
        supper: docSnap.exists() ? docSnap.data().count || 0 : 0,
      }));
    });

    return () => {
      unsubBreakfast();
      unsubLunch();
      unsubSupper();
    };
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>üìä Admin Dashboard</h2>
      <h3>Live Meal Counts</h3>

      <p>üç≥ Breakfast: {mealCounts.breakfast}</p>
      <p>üçõ Lunch: {mealCounts.lunch}</p>
      <p>üç≤ Supper: {mealCounts.supper}</p>
    </div>
  );
}

export default AdminPage;
