// src/StudentPage.js
import React, { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";

function StudentPage() {
  const [meals, setMeals] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });
  const [loading, setLoading] = useState(true);

  // Load saved meals from Firestore
  useEffect(() => {
    const fetchData = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const docRef = doc(db, "mealMarks", user.uid);
      const snap = await getDoc(docRef);

      if (snap.exists()) {
        setMeals(snap.data());
      }
      setLoading(false);
    };

    fetchData();
  }, []);

  // Toggle meal on/off
  const toggleMeal = async (meal) => {
    const user = auth.currentUser;
    if (!user) return;

    const updated = { ...meals, [meal]: !meals[meal] };
    setMeals(updated);

    await setDoc(doc(db, "mealMarks", user.uid), updated);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Student Page</h2>
      {["breakfast", "lunch", "supper"].map((meal) => (
        <button
          key={meal}
          onClick={() => toggleMeal(meal)}
          style={{
            margin: "10px",
            padding: "10px 20px",
            backgroundColor: meals[meal] ? "green" : "gray",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          {meal.charAt(0).toUpperCase() + meal.slice(1)}{" "}
          {meals[meal] ? "✅" : "❌"}
        </button>
      ))}
    </div>
  );
}

export default StudentPage;
